import { Injectable } from "@angular/core";
import { Actions, createEffect, ofType } from "@ngrx/effects";
import { BoardsService } from "src/app/modules/boards/services/boards.service";
import { createBoard, createBoardSuccess, deleteBoard, deleteBoardSuccess, editBoard, editBoardSuccess, getBoards, getBoardsSuccess } from "../actions/boards.action";
import { catchError, map, of, switchMap } from "rxjs";
import { Board, BoardName } from "src/app/modules/boards/models/board";

@Injectable()
export class BoardsEffects {
    constructor(private actions$: Actions, private boardsService: BoardsService){}

    getBoards$ = createEffect(() => this.actions$.pipe(
        ofType(getBoards),
        switchMap(() => {
            return this.boardsService.getBoards().pipe(map((data) => getBoardsSuccess({ allBoards: data })))
        })
    ));

    createBoard$ = createEffect(() => this.actions$.pipe(
        ofType(createBoard),
        switchMap((action) => {
            return this.boardsService.createBoard(action.board.name!).pipe(
                map((data: any) => {
                    const board: BoardName = {
                        id: data.id,
                        name: data.name
                    };
                    return createBoardSuccess({ board });
                })
            );
        })
    ));

    deleteBoard$ = createEffect(() => this.actions$.pipe(
        ofType(deleteBoard),
        switchMap((action) => {
            return this.boardsService.deleteBoard(action.id).pipe(
                switchMap(() => {
                    return of(deleteBoardSuccess({ id: action.id }));
                })
            );
        })
    ));

    editBoard$ = createEffect(() => this.actions$.pipe(
        ofType(editBoard),
        switchMap((action) => this.boardsService.editBoard(action.payload).pipe(
            map(() => {
                return editBoardSuccess({ board: action.payload });
            })
        ))
    ));
}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { environment } from 'src/environments/environment';
import { Board, BoardName, EditBoard } from '../models/board';
import { BehaviorSubject, Observable, map } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class BoardsService {
  baseUrl = environment.apiUrl + 'boards/';
  boardSubject: BehaviorSubject<Board | null> = new BehaviorSubject<Board | null>(null);
  board$: Observable<Board | null> = this.boardSubject.asObservable();

  boardNamesSubject: BehaviorSubject<BoardName[] | null> = new BehaviorSubject<BoardName[] | null>(null);
  boardNames$: Observable<BoardName[] | null> = this.boardNamesSubject.asObservable();

  constructor(private http: HttpClient){}

  getBoards(){
    return this.http.get<BoardName[]>(this.baseUrl).pipe(
      map(response => {
        this.boardNamesSubject.next(response);
        return response;
      })
    )
  }

  getBoard(id: number){
    return this.http.get<Board>(this.baseUrl + id).pipe(
      map(response => {
        this.boardSubject.next(response);
        return response;
      })
    )
  }

  createBoard(name: string){
    return this.http.post(this.baseUrl, {name});
  }

  editBoard(editedBoard: EditBoard){
    return this.http.patch(this.baseUrl, {id: editedBoard.id, name: editedBoard.name});
  }

  deleteBoard(id: number){
    return this.http.delete(this.baseUrl + id);
  }

  setBoard(board: Board){
    this.boardSubject.next(board);
  }

  setBoardNames(boards: BoardName[]){
    this.boardNamesSubject.next(boards);
  }
}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Card } from '../models/card';
import { environment } from 'src/environments/environment';

@Injectable({
  providedIn: 'root'
})
export class CardsService {
  private baseUrl = environment.apiUrl + 'cards/';

  constructor(private http: HttpClient){}

  createCard(card: Partial<Card>){
    return this.http.post(this.baseUrl, card);
  }

  editCard(id: number, card: Partial<Card>){
    return this.http.patch(this.baseUrl, {id: id, ...card});
  }

  deleteCard(id: number){
    return this.http.delete(this.baseUrl + id);
  }
}

import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class FormatDateService {

  constructor() {}

  formatDate(dateString: string): string {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = ('0' + (date.getMonth() + 1)).slice(-2);
    const day = ('0' + date.getDate()).slice(-2);
    return `${year}-${month}-${day}`;
  }
}

import { HttpClient, HttpParams } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Activity, ActivityResponse } from '../models/activity';
import { environment } from 'src/environments/environment';

@Injectable({
  providedIn: 'root'
})
export class HistoryService {
  baseUrl = environment.apiUrl + 'activity/';

  constructor(private http: HttpClient){}

  getLoggedActivity(boardId: number, pageSize: number){  
    let params = new HttpParams().set('pageSize', pageSize.toString());
    params = params.set('boardId', boardId.toString());

    return this.http.get<ActivityResponse>(this.baseUrl, {params});
  }

  getLoggedActivityByCardId(cardId: number){
    return this.http.get<Activity[]>(this.baseUrl + cardId);
  }
}

